sys_script_include.script:
var depNow_getUSDetails = Class.create();
depNow_getUSDetails.prototype = Object.extendsObject(AbstractAjaxProcessor, {

	getRunbookDetails : function() {
		var queryString = this.getParameter('sysparm_query');	
		var SourceURI=this.getParameter('sysparm_sourceURI');
		var request = new sn_ws.RESTMessageV2();
		request.setEndpoint(SourceURI+'/api/135654/depnow_sourceapi/fetchUpdateSet');
		request.setHttpMethod('GET');
		var user = gs.getProperty('depNow_user'); //'integration.deployer';
		var password = gs.getProperty('depNow_password'); //'integration@1234';
		request.setQueryParameter("queryString",queryString);
		request.setBasicAuth(user,password);
		request.setRequestHeader("Accept","application/json");		
		var response = request.execute();
		return response.getBody();
	},

	type: 'depNow_getUSDetails'
});
